window.addEventListener("DOMContentLoaded",function(){const e=document.querySelector(".hamburger"),t=document.querySelector(".nav-links"),o=document.querySelectorAll(".nav-links li"),n=document.querySelector(".header__menu");e.addEventListener("click",()=>{t.classList.toggle("open"),n.classList.toggle("open"),o.forEach(e=>{e.classList.toggle("fade")}),e.classList.toggle("toggle")}),document.querySelectorAll('a[href^="#"]').forEach(e=>{e.addEventListener("click",function(e){e.preventDefault(),document.querySelector(this.getAttribute("href")).scrollIntoView({behavior:"smooth"})})})});